import React, { useState } from 'react';

import { FaArrowLeft, FaArrowCircleDown } from 'react-icons/fa';
import { FiEye, FiEyeOff } from 'react-icons/fi';

// import Button from '../../components/Button';
import Input from '../../components/inputs/index';
import FotoAviso from '../../assets/images/Aviso.svg';
import Helmet from 'react-helmet';
import * as Yup from 'yup';
import '../../global.css';
import './style.css';
import { useHistory } from 'react-router-dom';
import Axios from 'axios';
import { Formik, Form } from 'formik';
import Button from '../../components/Button';


function CadastroAdm() {

    const [nome, setNome] = useState("");
    const [cpf, setCpf] = useState("");
    const [rg, setRg] = useState("");
    const [email, setEmail] = useState("");
    const [senha, setSenha] = useState("");

    const history = useHistory();

    const dataAdm = {
        nome,
        cpf,
        rg,
        email,
        senha,
    }





    const api = Axios.create({
        baseURL: "http://localhost:5000/api",
        headers: {
            'content-type': 'application/json',
            authorization: 'Bearer ' + localStorage.getItem("token-conectando")
        }
    })

    return (

        <Formik
            initialValues={
                {
                    nome: '',
                    email: '',
                    senha: '',
                    rg: '',
                    cpf: '',
                    cSenha: '',
                }}
            validationSchema={Yup.object({
                nome: Yup.string()
                    .max(15, 'Deve ter no máximo 15 caractéres')
                    .required('Required'),
                cpf: Yup.string()
                    .length(11, 'CPF inválido')
                    .required('Required'),
                rg: Yup.string()
                    .length(12, 'Tamanho inválido')
                    .required('Required'),
                email: Yup.string()
                    .email('email inválido')
                    .required('Required'),
                senha: Yup.string()
                    .min(3, 'Tamanho deve ser entre 3 a 8')
                    .max(8, 'Tamanho deve ser entre 3 a 8')
                    .required('Required'),
                cSenha: Yup.string()
                    .min(3, 'Tamanho deve ser entre 3 a 8')
                    .max(8, 'Tamanho deve ser entre 3 a 8')
                    .required('Required'),

            })}
            onSubmit={async (values, { setSubmitting }) => {
                if (values.senha === values.cSenha) {
                    try {
                        await api.post('/administrador', dataAdm, {
                            headers: {
                                'Content-Type': 'application/json',
                            }
                        }).then(res => { })
                        setTimeout(() => {
                            alert(JSON.stringify(values, null, 2));
                            setSubmitting(false);
                        }, 400);
                        history.push('/');
                    } catch (err) {
                        alert('Erro no cadastro, tente novamente');
                    }
                }
                else {
                    alert("Verifique se as senhas conferem!")
                }
            }}
        >

            <div className="tudoo" >
                <Helmet>
                    <style>
                        {` body{
                        background-color: #F4F4F4;
                    }`}

                    </style>
                </Helmet>
                <header className="headerAdm">
                    <div className="meio">
                        <div className="arrow">
                            <FaArrowLeft color="white" size={42} />
                        </div>
                        <div className="inverter">
                            <h1>Cadastre um novo<br />Adiministrador!</h1><br />
                            <h3>O primeiro passo é preencher esse formulário de cadastro.</h3>
                        </div>
                        <div className="tabela">
                            <div className="arrumarTabela">


                                <Form>
                                    <h2>Dados da Pessoa</h2>
                                    <div id="linha"></div>
                                    <Input label="Nome" name="nome" />
                                    <Input label="Email" name="email" />
                                    <Input label="CPF" name="cpf" />
                                    <Input label="RG" name="rg" />
                                    <div id="linha">
                                    </div>
                                    <div style={{ display: "flex", justifyContent: "space-between", padding: "2em" , }}>
                                        <Input label="Senha" name="senha" style={{ width: "13vw" }} />
                                        <Input label="Confirmar Senha" name="cSenha" style={{ width: "13vw" }} />
                                    </div>
                                </Form>
                            </div>

                        </div>
                        <div className="bottom">
                            <div id="together">
                                <img src={FotoAviso} alt="aviso" />
                                <p>Preencha todos <br /> os campos</p>
                            </div>
                            
                                <input className="botao" value="Cadastrar" />
                            
                        </div>
                    </div>
                </header>
            </div>
        </Formik>

    );
}


export default CadastroAdm;
