import React, { useState, useEffect } from 'react';
import { FaArrowRight } from 'react-icons/fa';
import { MdKeyboardArrowDown } from 'react-icons/md';

import ImgMicrosoft from '../../../assets/images/microsoft.png';

import Footer from '../../../components/footer';
import HeaderEmpresa from '../../../components/headerEmpresa';

import '../../../global.css';
import './style.css';
import ModeloVaga from '../../../components/ModeloVaga';
import { Link } from 'react-router-dom';
import api from '../../../services/api';
import ReactDOM from 'react-dom';


function VagasEmpresa() {

    const [Nome, setNome] = useState();
    const [vaga, setVaga] = useState();

    useEffect(() => {
        GetVagasEmpresa();
    }, [])

    const GetVagasEmpresa = () => {
        api.get('/empresa')
            .then(res => {
               ReactDOM.render(DetalhesDaVaga(res.data),document.getElementById("listarVaga"))
            })
    }

    const DetalhesDaVaga = (item:any) =>{
        item.map((vaga:any) =>{
            return(
                <div className="Offvagas">
                <div className="separar">
                <img src={ImgMicrosoft} alt="" id="img" />
                <h2>{vaga.nome} </h2>
            </div>
            <div className="dentro">
                <MdKeyboardArrowDown size={24} />
            </div>
        </div>
        
            )
        })
    }




    return (
        < >
            <HeaderEmpresa />
            <div className="midVagasEmpresa">
                <div className="topp" >

                    <div className="arrowdois">
                        <FaArrowRight color="black" size={24} />
                    </div>

                    <div id="head">
                        <h1>Seu perfil</h1>
                        <h1>Suas vagas</h1>
                    </div>
                    <div id="linhanav"></div>
                </div>


                <div className="Offvagas" id="listaVaga">
                   
                </div>
                
                {/* <ModeloVaga /> */}
                </div>
                <Footer />
        </>
    );
}

export default VagasEmpresa;
