import React, { useEffect, useState } from 'react';

import { FaArrowLeft } from 'react-icons/fa';

import Button from '../../components/Button';
import Input from '../../components/inputs/index';
import FotoAviso from '../../assets/images/Aviso.svg'

import '../../global.css';
import './style.css';

import { Formik, Form } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import api from '../../services/api';
import { isConstructorDeclaration } from 'typescript';




function CadastroEmpresa() {

    // useEffect(() => {
    //     getEmpresa();
    // }, [])

    // const getEmpresa = () => {
    //     api.get('/empresa')
    //         .then(res => {

    //         })
    // }

    return (
        <Formik
            initialValues={
                {
                    nomeFantasia: '',
                    razaoSocial: '',
                    porteempresa: '',
                    email: '',
                    cnpj: '',
                    cnae: '',
                    wpp: '',
                    foto: '',
                    senha: '',
                    confirmarsenha: '',
                    estado: '',
                    cidade: '',
                    cep: '',
                    bairro: '',
                    rua: '',
                    numero: ''
                }}
            validationSchema={Yup.object({
                nomeFantasia: Yup.string()
                    .max(20, 'Deve ter no máximo 20 caractéres')
                    .required('Required'),
                razaoSocial: Yup.string()
                    .max(20, 'Deve ter no máximo 20 caractéres')
                    .required('Required'),
                cnpj: Yup.string()
                    .max(14, 'Deve ter no máximo 14 caractéres')
                    .required('Required'),
                cnae: Yup.string()
                    .max(9, 'Deve ter no máximo 9 caractéres')
                    .length(9, 'CNAE inválido')
                    .required('Required'),
                wpp: Yup.string()
                    .min(12, 'Whatsapp inválido')
                    .required('Required'),
                senah: Yup.string()
                    .min(12, 'Whatsapp inválido')
                    .required('Required'),
                confirmarsenha: Yup.string()
                    .min(12, 'Whatsapp inválido')
                    .required('Required'),
                estado: Yup.string()
                    .min(9, 'Estado inválido')
                    .required('Required'),
                cidade: Yup.string()
                    .required('Required'),
                cep: Yup.string()
                    .notRequired(),
                bairro: Yup.string()
                    .required('Required'),
                rua: Yup.string()
                    .required('Required'),
                numero: Yup.string()
                    .required('Required'),
            })}
            onSubmit={async (data, { setSubmitting }) => {
                setSubmitting(true);
                await api.post('/empresa', data)
                    .then(res => {

                        res.data();
                    })
                setSubmitting(false);
            }}
        >
            {({ values, isSubmitting }) => (
                <div className="tudo" >

                    <header>
                        <div className="midCadastro">
                            <div className="arrow">
                                <FaArrowLeft color="white" size={42} />
                            </div>
                            <div className="inverter">
                                <h1>Seja bem vindo a familia SENAI!</h1><br />
                                <h3>O primeiro passo é preencher esse formulário de cadastro.</h3>
                            </div>

                            <Form>

                                <div className="tabelaEmpresa">
                                    <div className="arrumarTabela">
                                        <h2>Seus Dados</h2>
                                        <div id="linha"></div>

                                        <Input label="Nome fantasia" name="nomeFantasia" />
                                        <Input label="Razão social" name="razaoSocial" />
                                        <Input label="Porte da empresa" name="porteempresa" />
                                        <Input label="Email" name="email" />
                                        <Input label="CNPJ" name="cnpj" />
                                        <Input label="CNAE" name="cnae" />
                                        <Input label="Whatsapp" name="wpp" />
                                        <Input label="Foto" name="foto" />

                                        <div className="senhaEmpresa">
                                            <Input label="Senha" name="senha" />
                                            <Input label="Confirme sua senha" name="confirmarsenha" />
                                        </div>

                                        <h2>Seu endereço</h2>
                                        <div id="linha"></div>
                                        <div id="estadoEmpresa">
                                            <Input label="Estado" name="estado" />
                                            <Input label="Cidade" name="cidade" />
                                        </div>
                                        <Input label="CEP" name="cep" />
                                        <Input label="Bairro" name="bairro" />
                                        <div id="numeroEmpresa">
                                            <Input label="Rua" name="rua" />
                                            <Input label="Número" name="numero" />

                                        </div>
                                    </div>
                                </div>
                                <div className="bottom">
                                    <div id="together">
                                        <img src={FotoAviso} alt="aviso" />
                                        <p>Preencha todos <br /> os campos</p>
                                    </div>
                                    <button type="submit" disabled={isSubmitting}>Cadastrar</button>
                                </div>
                            </Form>
                        </div>
                    </header>
                </div>
            )}
        </Formik>
    );
}

export default CadastroEmpresa;
