import React, { useState, useEffect } from 'react';
import { FaArrowRight } from 'react-icons/fa';
import { MdKeyboardArrowDown } from 'react-icons/md';

import ImgMicrosoft from '../../../assets/images/microsoft.png';

import Footer from '../../../components/footer';
import HeaderEmpresa from '../../../components/headerEmpresa';

import '../../../global.css';
import './style.css';
import ModeloVaga , { VagaModel } from '../../../components/ModeloVaga';
import { Link } from 'react-router-dom';
import api from '../../../services/api';
import ReactDOM from 'react-dom';


function VagasEmpresa() {

    const [Nome, setNome] = useState();
    const [vagas, setVaga] = useState([{},[]]);

    useEffect(() => {
        GetVagasEmpresa();
    }, [])
    const auth = `Bearer ${localStorage.getItem('conectando-key-auth')}`;

    const GetVagasEmpresa = () => {
        api.get('/empresa/1', { headers: { authorization: auth } })
            .then(res => {
                console.log(res.data);
                setVaga(res.data);
                ReactDOM.render(DetalhesDaVaga(res.data.vaga.nome), document.getElementById("listarVaga"))
            })
    }



    const DetalhesDaVaga = (item: any) => {
        return (
            <div>
                {item.map((vaga: any) => (
                    <div className="Offvagas">
                        <div className="separar">
                            <img src={ImgMicrosoft} alt="" id="img" />
                            <h2>{vaga.nome} </h2>
                        </div>
                        <div className="dentro">
                            <MdKeyboardArrowDown size={24} />
                        </div>
                    </div>
                ))}
            </div>

        );
    }




    return (
        < >
            <HeaderEmpresa />
            <div className="midVagasEmpresa">
                <div className="topp" >

                    <div className="arrowdois">
                        <FaArrowRight color="black" size={24} />
                    </div>

                    <div id="head">
                        <h1>Seu perfil</h1>
                        <h1>Suas vagas</h1>
                    </div>
                    <div id="linhanav"></div>
                </div>

    

            </div>
            <Footer />
        </>
    );
}

export default VagasEmpresa;
