import React, { useEffect, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import {  FiAlertCircle, FiArrowLeft  } from 'react-icons/fi';
import Helmet from 'react-helmet';

import { Formik, Form } from 'formik';
import * as Yup from 'yup';

import './styles.css';
import Input from '../../components/inputs';
import api from '../../services/api';
import Axios from 'axios';
import Select from 'react-select';

function CadastroAluno() {
    const history = useHistory();
    
    //#region States
        const [nome, setNome] = useState("");
        const [cpf, setCpf] = useState("");
        const [rg, setRg] = useState("");
        const [email, setEmail] = useState("");
        const [whatsapp, setWhatsapp] = useState("");
        const [deficiencia, setDeficiencia] = useState("");
        const [deficienciaDescricao, setDeficienciaDescricao] = useState("");
        const [senha, setSenha] = useState("");
        const [nivelExp, setNivelExp] = useState("");
        const [focoCarreira, setFocoCarreira] = useState("");
        const [idCurso, setIdCurso] = useState(0);
        const [semestre, setSemestre] = useState(0);
        const [linkPortfolio, setLinkPortfolio] = useState("");
        const [prefContrato, setPrefContrato] = useState("");
        const [prefPorte, setPrefPorte] = useState("");
        const [prefRemoto, setPrefRemoto] = useState("");
        const [idEnderecoNavigation, setIdEnderecoNavigation] = useState([]);
        const [uf,  setUf] = useState("");
        const [cidade, setCidade] = useState("");
        const [cep, setCep] = useState("");     
        const [rua, setRua] = useState("");     
        const [bairro, setBairro] = useState("");
        const [numero, setNumero] = useState("");
        const [options, setOptions] = useState([{},[]]);
    //#endregion

    var optionsBanco: { value: any; label: any; }[] = [];


    useEffect(()=>{
        handleLoad();
    },[]);

    function handleLoad(){
        api.get('/Tags', {
            headers: {
                'content-type': 'application/json'
            }
        }).then(res => {
            res.data.forEach((element:any) => {
                Montar(element)
            })
        }).catch(err => console.log(err))
    }


    const dataAluno = {
        nome,
        cpf,
        rg,
        email,
        whatsapp,
        deficiencia,
        deficienciaDescricao,
        senha,
        nivelExp,
        focoCarreira,
        idCurso,
        semestre,
        linkPortfolio,
        prefContrato,
        prefPorte,
        prefRemoto,
        idEnderecoNavigation: {
            uf,
            cidade,
            cep,
            rua,
            bairro,
            numero,
        },
    }

    const Montar = (e:any) => {
        optionsBanco.push({ value: e.idTags, label: e.nome});
        setOptions(optionsBanco);
    }

    const CadastrarTagAluno = (idAluno:any) => {
        let tagsPorNome = document.getElementsByName('tags');
        for(let index = 0; index < tagsPorNome.length; index++) {

            const tagsAluno = {idAluno: idAluno, idTags: tagsPorNome[index].attributes[2].value};

            api.post('/TagsAluno', tagsAluno, {
                headers: {
                    'Content-Type': 'application/json',
                }
            })
        }
    }

    const GetCep = (valor: string) => {
        Axios.get(`https://viacep.com.br/ws/${valor}/json/`)
            .then(res => {
                var teste = res.data.cep;
                var cep = teste.replace("-", "")

                setCep(cep);
                setRua(res.data.logradouro);
                setBairro(res.data.bairro);
                setCidade(res.data.localidade)
                setUf(res.data.uf)
            })
            .catch(erro => console.error(erro))
    }

    return(
        <div id="page-form" className="containerCadastro">
        <Helmet>
            <style>
                {` body{
                    background-color: #F4F4F4;
                }`}
            </style>
        </Helmet>
            <header className="page-header">
                <div className="top-bar-container">
                    <Link to="/">
                        <FiArrowLeft size={50} color="#FFF"/>
                    </Link>
                </div>

                <div className="header-content">
                    <strong>Seja bem vindo a família Senai!</strong>
                    <p>O primeiro passo é preencher esse formulário de cadastro.</p>
                </div>
            </header>

            <main>
                        <Formik
                            initialValues={
                                { 
                                    nome,
                                    cpf,
                                    rg,
                                    email,
                                    whatsapp,
                                    deficiencia,
                                    deficienciaDescricao,
                                    senha,
                                    confirmarSenha: '',
                                    uf,
                                    cidade,
                                    cep,
                                    rua,
                                    bairro,
                                    numero,
                                    nivelExp,
                                    idCurso,
                                    focoCarreira,
                                    semestre,
                                    linkPortfolio,
                                    prefContrato,
                                    prefPorte,
                                    prefRemoto,
                                    idEnderecoNavigation,
                                }}
                            validationSchema={Yup.object({
                                nome: Yup.string()
                                .max(15, 'Deve ter no máximo 15 caractéres')
                                .required('Required'),
                                cpf: Yup.string()
                                .length(11, 'CPF inválido')
                                .required('Required'),
                                rg: Yup.string()
                                .length(12, 'Tamanho inválido')
                                .required('Required'),
                                email: Yup.string()
                                .email('email inválido')
                                .required('Required'),
                                whatsapp: Yup.string()
                                .min(9, 'Whatsapp inválidp')
                                .required('Required'),
                                deficiencia: Yup.string()
                                .notRequired(),
                                deficienciaDescricao: Yup.string()
                                .notRequired(),
                                senha: Yup.string()
                                .min(3, 'Tamanho deve ser entre 3 a 8')
                                .max(8, 'Tamanho deve ser entre 3 a 8')
                                .required('Required'),
                                confirmarSenha: Yup.string()
                                .min(3, 'Tamanho deve ser entre 3 a 8')
                                .max(8, 'Tamanho deve ser entre 3 a 8')
                                .required('Required'),
                                uf: Yup.string()
                                .max(4, 'UF inválida'),
                                cidade: Yup.string()
                                .max(30, 'Nome da cidade inválido'),
                                cep: Yup.string()
                                .length(8, 'CEP inválido')
                                .required('Required'),
                                rua: Yup.string()
                                .max(100, 'Tamanho inválido'),
                                bairro: Yup.string()
                                .notRequired(),
                                numero: Yup.string()
                                .max(6, 'Número maior que o devido')
                                .required('Required'),
                                idCurso: Yup.number()
                                .required('Required'),
                                focoCarreira: Yup.string()
                                .required('Required'),
                                semestre: Yup.number()
                                .positive('Semestre deve ser positivo')
                                .required('Required'),
                                linkPortfolio: Yup.string()
                                .notRequired(),
                                prefContrato: Yup.string()
                                .required('Required'),
                                prefPorte: Yup.string()
                                .required('Required'),
                                prefRemoto: Yup.string()
                                .required('Required'),
                            })}
                            onSubmit={async (values, { setSubmitting }) => {
                                if (values.senha === values.confirmarSenha) {
                                    try {
                                        await api.post('/aluno', dataAluno, {
                                            headers: {
                                                'Content-Type': 'application/json',
                                            }
                                        }).then(res => {CadastrarTagAluno(res.data.idAluno)})
                                        setTimeout(() => {
                                        alert(JSON.stringify(values, null, 2));
                                        setSubmitting(false);
                                        }, 400);
                                        history.push('/');
                                    } catch (err) {
                                        alert('Erro no cadastro, tente novamente');
                                    }
                                }
                                else{
                                    alert("Verifique se as senhas conferem!")
                                }
                            }}
                            >
                <Form>               
                    <fieldset>
                        <legend>Seus dados</legend>
                        <Input label="Nome" name="nome" onBlur={(e) => setNome(e.target.value)}/>
                        <Input label="CPF" name="cpf" onBlur={(e) => setCpf(e.target.value)}/>
                        <Input label="RG" name="rg" onBlur={(e) => setRg(e.target.value)}/>
                        <Input label="E-mail" name="email" onBlur={(e) => setEmail(e.target.value)}/>
                        <Input label="Whatsapp" name="whatsapp" onBlur={(e) => setWhatsapp(e.target.value)}/>
                        <div className="def">
                            <Input label="Possui deficiência?" name="deficiencia" as="select" onBlur={(e) => setDeficiencia(e.target.value)}>
                                <option disabled selected value="">Selecione uma opção</option>
                                <option value="Não">Não</option>
                                <option value="Sim">Sim</option>
                            </Input>
                                
                            <Input label="Se sim qual?" name="deficienciaDescricao" onBlur={(e) => setDeficienciaDescricao(e.target.value)}/>
                        </div>
                        <div className="senha">
                            <Input label="Senha" name="senha" onBlur={(e) => setSenha(e.target.value)}/>
                            <Input label="Confirmar senha" name="confirmarSenha"/>
                        </div>
                    </fieldset>

                    <fieldset>
                        <legend>Seu endereço</legend>
                        <Input label="CEP" name="cep" onBlur={(e) => GetCep(e.target.value)}/>
                        <div className="estadoCidade">
                            <Input label="Estado" name="uf" value={uf} onBlur={(e) => setUf(e.target.value)}/>
                            <Input label="Cidade" name="cidade" value={cidade} onBlur={(e) => setCidade(e.target.value)}/>
                        </div>
                        <Input label="Bairro" name="bairro" value={bairro} onBlur={(e) => setBairro(e.target.value)}/>
                        <div className="ruaNumero">
                            <Input label="Rua" name="rua" value={rua} onBlur={(e) => setRua(e.target.value)}/>
                            <Input label="Número" name="numero" onBlur={(e) => setNumero(e.target.value)}/>
                        </div>
                    </fieldset>

                    <fieldset>
                        <legend>Sua carreira</legend>
                        <Input label="Curso" name="idCurso" as="select" onBlur={(e) => setIdCurso(parseInt(e.target.value))}>
                            <option disabled selected value="0">Selecione uma opção</option>
                            <option value="1">Desenvolvimento de sistemas</option>
                            <option value="2">Redes de computadores</option>
                            <option value="3">Multimídia</option>
                        </Input>
                        
                        <Input label="Foco na área" name="focoCarreira" onBlur={(e) => setFocoCarreira(e.target.value)}/>
                        <Input label="Nível de experiência" name="nivelExp" onBlur={(e) => setNivelExp(e.target.value)}/>
                        <Input label="Semestre" name="semestre" as="select" onBlur={(e) => setSemestre(parseInt(e.target.value))}>
                            <option disabled selected value="0">Selecione uma opção</option>
                            <option value="1">1º semestre</option>
                            <option value="2">2º semestre</option>
                            <option value="3">3º semestre</option>
                        </Input>
                        <Input label="Seu portfófilo" name="linkPortfolio" onBlur={(e) => setLinkPortfolio(e.target.value)}/>
                        <div className="style-input-select style-input">
                        <label htmlFor="tags">Selecione suas melhores skills</label>
                        <Select 
                            className="basic-single"
                            clasNamePrefix="select"
                            name="tags"
                            isMulti
                            options={options}
                            id="tags"
                        />
                        </div>
                    </fieldset>

                    <fieldset>
                        <legend>Sua busca de vagas</legend>
                        
                        <Input label="Tipo de contrato" name="prefContrato" as="select" onBlur={(e) => setPrefContrato(e.target.value)}>
                            <option disabled selected value="">Selecione uma opção</option>
                            <option value="Estágio">Estágio</option>
                            <option value="CLT">CLT</option>
                            <option value="Freelancer">Freelancer</option>
                        </Input>
                        
                        <Input label="Tamanho da empresa" name="prefPorte" as="select" onBlur={(e) => setPrefPorte(e.target.value)}>
                            <option disabled selected value="">Selecione uma opção</option>
                            <option value="Pequena">Pequena</option>
                            <option value="Média">Média</option>
                            <option value="Grande">Grande</option>
                        </Input>

                        <Input label="Trabalho remoto?" name="prefRemoto" as="select" onBlur={(e) => setPrefRemoto(e.target.value)}>
                            <option disabled selected value="">Selecione uma opção</option>
                            <option value="Não">Não</option>
                            <option value="Sim">Sim</option>
                        </Input>
                    </fieldset>

                    <footer>
                        <p>
                            <FiAlertCircle size={24} color="#FF0000"/>
                            Importante! <br/>
                            Preencha todos os dados
                        </p>
                        <button type="submit">
                            Salvar cadastro
                        </button>
                    </footer>
                </Form>
                            </Formik>
            </main>
        </div>
    )
}

export default CadastroAluno;
