import React, { useState, useEffect } from 'react';
import { FaArrowRight } from 'react-icons/fa';

import { motion } from 'framer-motion';

import ImgMicrosoft from '../../../assets/images/microsoft.png';

import Footer from '../../../components/footer';
import HeaderEmpresa from '../../../components/headerEmpresa';

import '../../../global.css';
import './style.css';
import ModeloVaga, { VagaModel } from '../../../components/ModeloVaga';
import { Link } from 'react-router-dom';
import api from '../../../services/api';
import ReactDOM from 'react-dom';
import Axios from 'axios';


function VagasEmpresa() {

    const pageTransition = {
        duration: 1
    }

    const pageVariante = {
        in: {
            opacity: 1,
            x: 0
        },
        out: {
            opacity: 0,
            x: "-180vw"
        }
    }


    const api = Axios.create({
        baseURL: "http://localhost:5000/api",
        headers: {
            'content-type': 'application/json',
            authorization: 'Bearer ' + localStorage.getItem("token-conectando")
        }
    })

    const [vagas, setVagas] = useState([]);
    const [empresa, setEmpresa] = useState([]);

    useEffect(() => {
        GetVagasEmpresa();
    }, []);

    const MaisDetalhes = (id: any) => {
        localStorage.setItem('idVaga', id)
        window.location.href = "/DetalhesVagaEmpresa"
    }


    const auth = `Bearer ${localStorage.getItem('conectando-key-auth')}`;

    async function GetVagasEmpresa() {
        await api.get('/Vaga',
            {
                headers: {
                    'Accept': 'application/json, text/plain, */*',
                    'Content-Type': 'application/json',
                    authorization: auth
                }
            })
            .then(res => {
                console.log(res.data);
                setEmpresa(res.data);
                setVagas(res.data);
            })
    }



    // const DetalhesDaVaga = (vagas: any) => {
    //     return (
    //         <div>
    //             {vagas.map((data: any) => (
    //                 <>
    //                 <div className="Offvagas">
    //                     <div className="separar">
    //                         <img src={ImgMicrosoft} alt="" id="img" />
    //                         <h2>{data.email} </h2>
    //                     </div>
    //                     <div className="dentro">
    //                         <MdKeyboardArrowDown size={24} />
    //                     </div>
    //                 </div>
    //                 </>
    //                 ))}
    //         </div>

    //     );
    // }




    return (
        < >
            <HeaderEmpresa />
            <div className="midVagasEmpresa">
                <div className="topp" >

                    <div className="arrowdois">
                        <FaArrowRight color="black" size={24} />
                    </div>

                    <div id="head">
                        <h1>Seu perfil</h1>
                        <h1>Suas Vagas</h1>
                    </div>
                    <div id="linhanav"></div>
                </div>
                <motion.div exit="out" animate="in" initial="out" variants={pageVariante} transition={pageTransition}>
                    <div className="Offvagas">
                        {empresa.map((data: any) => (
                            <div className="Offvagas">
                                <div className="separar">
                                    <img src={ImgMicrosoft} alt="" id="img" />
                                    <h2>{data.nome} </h2>
                                </div>
                                <div className="dentro">
                                    <p onClick={() => { MaisDetalhes(empresa) }}>MaisDetalhes</p>
                                </div>
                            </div>

                        ))}
                    </div>
                    {vagas.map((modelo: VagaModel) => {
                        return <ModeloVaga key={modelo.idVaga} modelo={modelo} />
                    })}
                </motion.div>
            </div>
            <Footer />
        </>
    );
}

export default VagasEmpresa;
