import React, { useState } from 'react';
import Input from '../../components/inputs/index';
import conectandoImg from '../../assets/images/LogoSemFundo.svg';
import senaiImg from '../../assets/images/logoVermelho.svg';
import { FiEye } from 'react-icons/fi';

import './styles.css';
import { Link } from 'react-router-dom';
import api from '../../services/api';
import { Formik, Form } from 'formik';
import { BiHandicap } from 'react-icons/bi';
import { JsxEmit } from 'typescript';


function Login() {

    const [entrada, setEntrada] = useState('');
    const [senha, setSenha] = useState('');

    const login = () => {
        const login = {
            email: entrada,
            senha: senha
        }

        api.post('/login')
            .then(res => {
                if (res.data.token != undefined) {
                    localStorage.setItem('token-conectando', res.data.token)
                } else {
                    alert("Senha ou email incorretos!")
                }
            }).catch(err => console.log(err));
    }
  

    return (
        <Formik
            initialValues={
                {
                    email: '',
                    senha: '',
                }}
            onSubmit={(data, {setSubmitting}) => {
             setSubmitting(true);

             console.log(data)
             setSubmitting(false);
            }}
            
        >
            {({ values,isSubmitting, handleSubmit, handleChange }) => (
                <div className="containerLogin">
                    <div className="logos">
                        <img src={conectandoImg} alt="Logo conectando" />
                        <img src={senaiImg} alt="Logo senai vermelho" />
                    </div>
                    <div className="form">
                        <h1>Login</h1>
                        <form>
                            <Input label="E-mail / CPF / CNPJ" name="email" />
                            <Input label="Senha" name="senha" />
                            <button type="submit" disabled={isSubmitting} >Entrar</button>
                        </form>
                        <pre>{JSON.stringify(values, null, 2)}</pre>
                        <span>NÃ£o tem cadastro?</span>
                        <span>Entre como<Link to="">&nbsp;EMPRESA&nbsp;</Link> ou <Link to="/cadastro">&nbsp;ALUNO&nbsp;</Link></span>
                    </div>
                </div>
            )}
        </Formik>
        

    );
}

export default Login;
