import React, { useState, useEffect } from 'react';
import Footer from '../../../components/footer';
import { FiArrowLeft, FiBarChart2, FiBriefcase, FiClipboard, FiClock, FiDollarSign, FiHome, FiList, FiMap, FiPlusCircle, FiTag, FiTerminal, FiTrendingUp } from 'react-icons/fi';
import empresaImg from '../../../assets/images/empresa126.svg';

import './styles.css';
import Tag from '../../../components/Tag';
import HeaderEmpresa from '../../../components/headerEmpresa';
import jwt_decode from 'jwt-decode';

import api from '../../../services/api';
import { date } from 'yup';
import { isConstructorDeclaration } from 'typescript';

function DetalhesVagaEmpresa() {

    const auth = `Bearer ${localStorage.getItem('conectando-key-auth')}`;

    const [empresa, setNome] = useState('');

    // const [area, setArea] = useState('');
    // const [nivelExp, setNivelExp] = useState('');
    // const [tipoContrato, setTipoContrato] = useState('');
    // const [remoto, setRemoto] = useState('');
    // const [detalhes, setDetalhes] = useState('');
    // const [requisitos, setRequisitos] = useState('');
    // const [porteEmpresa, setPorteEmpresa] = useState('');
    // const [horario, setHorario] = useState(date());
    // const [salario, setSalario] = useState('');
    // const [beneficios, setBeneficios] = useState('');
    // const [prazo, setPrazo] = useState('');
    // const [limiteAluno, setLimiteAluno] = useState('');
    // const [situacao, setSituacao] = useState('');
    // const [idEndereco, setIdEndereco] = useState('');
    // const [uf, setUf] = useState('');
    // const [cidade, setCidade] = useState('');
    // const [idEmpresa, setIdEmpresa] = useState('');

    // var optionsBanco: { value: any; label: any; }[] = [];

    // const Montar = (e: any) => {
    //     optionsBanco.push({ value: e.idTags, label: e.nome });
    //     setOptions(optionsBanco);
    // }

    // function handleLoadTags() {
    //     api.get('/Tags', {
    //         headers: {
    //             'content-type': 'application/json'
    //         }
    //     }).then(res => {
    //         res.data.forEach((element: any) => {
    //             Montar(element)
    //         })
    //     }).catch(err => console.log(err))
    // }

    const BuscarTags = (tags: any) => {
        let tagsEmpresa: any = []
        let pTags: string = ''
        for (let index = 0; index < tags.length; index++) {
            api.get('/Tags/' + tags[index].idTags)
                .then(response => {
                    tagsEmpresa.push(response.data.nome)
                    pTags = pTags + `<p>${response.data.nome}</p>`;
                    let div = document.getElementById('divTags')
                    if (div !== null) div.innerHTML = pTags;
                })
        }
    }


    function handleDecode() {
        const auth = `Bearer ${localStorage.getItem('conectando-key-auth')}`;
        var id = "";
        try {
            const authDec: any = jwt_decode(auth);
            id = authDec.jti;
            return id;
        } catch (error) {
            console.log("Erro no decode")
        }
    }

    useEffect(() => {
        onLoadVaga();
    }, [])

    function onLoadVaga() {
        api.get('/vaga/1', { headers: { authorization: auth } })
            .then((res) => {
                console.log(res);
                setNome(res.data.nome);
            })
    }



    return (
        <>

            <HeaderEmpresa />
            <div className="containerVaga">
                <div className="top">
                    <FiArrowLeft size={80} color="#4E4E4E" />
                    <img src={empresaImg} alt="" />
                </div>
                <div className="containerCinza">
                    <div className="header espaco">
                        <div className="empresa espaco">
                            <h2>Titulo vaga</h2>
                        </div>
                        <span>
                            <FiTag size={24} color="#4E4E4E" />
                            Area da vaga
                        </span>
                    </div>
                    <div className="infos espaco">
                        <ul>
                            <div>
                                <li><FiBriefcase size={24} color="#4E4E4E" /> {nome}  </li>
                                <li><FiBarChart2 size={24} color="#4E4E4E" /> {porteEmpresa} </li>
                                <li><FiMap size={24} color="#4E4E4E" /> {cidade}-{uf} </li>
                            </div>
                            <br />
                            <div>
                                <li><FiTrendingUp size={24} color="#4E4E4E" /> {nivelExp} </li>
                                <li><FiClipboard size={24} color="#4E4E4E" /> {tipoContrato} </li>
                                <li><FiHome size={24} color="#4E4E4E" /> Remoto: {remoto}</li>
                            </div>
                        </ul>
                    </div>
                </div>

                <div className="main">
                    <div className="detalhes">
                        <span>
                            <FiTerminal size={24} color="#4E4E4E" />
                            <h2>
                                Detalhes
                            </h2>
                        </span>
                        <p className="align espacoTitulos">
                            {detalhes}
                        </p>
                    </div>
                    <div className="meio">
                        <div className="requisitos">
                            <span>
                                <FiList size={24} color="#4E4E4E" />
                                <h2>
                                    Requisitos
                                </h2>
                            </span>
                            <ul className="align espacoTitulos">
                                <li>{requisitos}</li>
                            </ul>
                        </div>
                        <div className="complementos">
                            <span>
                                <FiClock size={24} color="#4E4E4E" />
                                <h3>
                                    Horário: <p>{horario}</p>
                                </h3>
                            </span>
                            <span>
                                <FiDollarSign size={24} color="#4E4E4E" />
                                <h3>
                                    Salário: <p>{salario}</p>
                                </h3>
                            </span>
                        </div>
                    </div>
                    <div className="bonus">
                        <span>
                            <FiPlusCircle size={24} color="#4E4E4E" />
                            <h2>
                                Bônus
                            </h2>
                        </span>
                        <p className="align espacoTitulos">{beneficios}</p>
                    </div>
                    <div className="tags">
