import React, {InputHTMLAttributes} from 'react';
import './style.css';
import '../../global.css';

import {  Field, ErrorMessage  } from 'formik';

interface InputProps extends InputHTMLAttributes<HTMLInputElement>{
    label: string;
    name: string;
    type?: string;
    as?: string;
}

const Input: React.FC<InputProps> = ({label, name, type,as, ...rest}) => {
    function showPassword(name:any){
        var pass:any = document.getElementById(`${name}`);
            if (pass.type === "password") {
                pass.type = "text";
            }
            else {
                pass.type = "password";
            }
    }

    return(
        <div className="style-input">
            <label htmlFor={name}>{label}</label><br/>
            <Field type={type} name={name} id={name}{...rest} as={as}/>
            <span onClick={showPassword} className="showPassword">Mostrar senha</span>
            <div className="error-message">
                <ErrorMessage name={name}/>
            </div>
        </div>
    );
}   

export default Input;
