            <div id="div_Tags_">
                {tags.map((t:any) => (
                    <>
                        <p className="TagsEstilo" onClick={() => AvisoTag(t.idTags)}>{t.nome}</p>
                    </>
                ))}
            </div>
        )
    }

    const AvisoTag = (id:any) => {
