    }


    return(
        <div>
            <HeaderAluno/>
            <div>
                <div className="div-menu">
                    <h1 onClick={() => window.location.href = "/confirmacaoEmpresa"}>Confirmação</h1>
                    <h1 className="sublinhar">Cadastro</h1>
                </div>

                <div id="tagsBuscadas">

                </div>

                <div>
                    <input type="text"/>
                    <button>Cadastrar Tag</button>
                </div>
            </div>

            <Footer/>
        </div>
    );
}

export default CadastrarTags;
