import React, { useState } from 'react';
import { FiBriefcase, FiHome, FiBarChart2, FiMap, FiTrendingUp, FiClipboard, FiTag, FiTerminal, FiChevronsRight } from 'react-icons/fi';
import empresaImg from '../../assets/images/empresa.png';
import axios from 'axios';
import './styles.css';
import jwt_decode from "jwt-decode";
import api from '../../services/api';

function ModeloVaga() {

    const auth = `Bearer ${localStorage.getItem('conectando-key-auth')}`;

    let decoded: any = jwt_decode(auth);

    console.log(decoded);

    const [nome, setNome] = useState('');
    const [nivelExp, setNivelExp] = useState('');
    const [tipoContrato, setTipoContrato] = useState('');
    const [remoto, setRemoto] = useState('');
    const [area, setArea] = useState('');
    const [detalhes, setDetalhes] = useState('');
    const [horario, setHorario] = useState('');
    const [salario, setSalario] = useState('');
    const [requisitos, setRequisitos] = useState('');
    const [bonus, setBonus] = useState('');

    const idVagaLocal = localStorage.getItem('idVaga')

    function load() {
        api.get('/vaga'+idVagaLocal, {
            headers: {
                'Accept': 'application/json, text/plain, /',
                'Content-Type': 'application/json',
                authorization: auth
            }
        })
            .then(function (response) {
                setNome(response.data.nome);
                setNivelExp(response.data.nivelExp);
                setTipoContrato(response.data.tipoContrato);
                setRemoto(response.data.remoto);
                setArea(response.data.area);
                setDetalhes(response.data.detalhes);
                setHorario(response.data.horario);
                setSalario(response.data.salario);
                setRequisitos(response.data.requisitos);
                setBonus(response.data.bonus);


                console.log(response.data);
            })
            .catch(function (error) {

                console.log(error);
            });
    }


    return (


        <div className="container">
            <div className="header espaco">
                <div className="empresa espaco">
                    <img src={empresaImg} alt="" />
                    <h2>{nome}</h2>
                </div>
                <span>
                    <FiTag size={24} color="#4E4E4E" />
                    Area da vaga
                </span>
            </div>
            <div className="infos espaco">
                <ul>
                    <div>
                        <li><FiBriefcase size={24} color="#4E4E4E" /> Nome empresa </li>
                        <li><FiBarChart2 size={24} color="#4E4E4E" /> Porte empresa </li>
                        <li><FiMap size={24} color="#4E4E4E" /> Estado - Cidade </li>
                    </div>
                    <br />
                    <div>
                        <li><FiTrendingUp size={24} color="#4E4E4E" /> Nível de exp. </li>
                        <li><FiClipboard size={24} color="#4E4E4E" /> Tipo de contrato </li>
                        <li><FiHome size={24} color="#4E4E4E" /> Remoto: Sim/Não</li>
                    </div>
                </ul>
            </div>
            <span className="detalhes espaco">
                <FiTerminal size={24} color="#4E4E4E" />
                <p>
                    Desenvolvimento de customizações em PLSQL/Oracle. Desenvolvimento de plugins, Desenvolvimento de plugins, Desenvolvimento de plugins...
                </p>
            </span>

            <span className="maisDetalhes">Mais detalhes<FiChevronsRight size={24} color="#FF0000" /></span>
        </div>
    )
}

export default ModeloVaga;
