import React, { useEffect, useState } from 'react';
import HeaderAluno from '../../../components/headerAluno/index';
import Footer from '../../../components/footer/index';
import { Link } from 'react-router-dom';
import './style.css';
import FotoAluno from '../../../assets/images/fotoPac.png';
import { FiTrendingUp, FiMap, FiHome } from "react-icons/fi";
import { motion } from 'framer-motion';
import jwt_decode from "jwt-decode";
import api from '../../../services/api';


function ListaAluno() {

    const pageTransition = {
        duration: 1
    }

    const pageVariante = {
        in: {
            opacity: 1,
            x: 0
        },
        out: {
            opacity: 0,
            x: "-180vw"
        }
    }



    const auth = `Bearer ${localStorage.getItem('conectando-key-auth')}`;

    let decoded: any = jwt_decode(auth);

    console.log(decoded);

    useEffect(() => (load()), [])

    const [nome, setNome] = useState('');
    const [nivelExp, setNivelExp] = useState('');
    const [tipoContrato, setTipoContrato] = useState('');
    const [prefRemoto, setRemoto] = useState('');
    const [area, setArea] = useState('');
    const [cidade, setCidade] = useState('');
    const [bairro, setBairro] = useState('');

    function load() {
        api.get('/aluno/1', {
            headers: {
                'Accept': 'application/json, text/plain, /',
                'Content-Type': 'application/json',
                authorization: auth
            }
        })
            .then(function (response) {
                setNome(response.data.nome);
                setNivelExp(response.data.nivelExp);
                setTipoContrato(response.data.tipoContrato);
                setRemoto(response.data.remoto);
                setArea(response.data.area);

                console.log(response.data);
            })
            .catch(function (error) {

                console.log(error);
            });
    }


    return (
        <div>
            <HeaderAluno />
            <nav className="menulista">
                <ul className="menu">
                    <li ><Link className="cool-link" to="/listaAluno">Aluno</Link></li>
                    <li><Link className="cool-link" to="/listaEmpresa">Empresa</Link></li>
                    <li><Link className="cool-link" to="/listaVaga">Vagas</Link></li><br />
                </ul>
                <div className="hr">
                    <hr />
                </div>
            </nav>
            <h5>Filtro</h5>

            <motion.div exit="out" animate="in" initial="out" variants={pageVariante} transition={pageTransition}>
                <div className="centro">

                    <div className="containerr baixo">
                        <div className="listaaa espacoo">
                            <div className="aluno espacoo">
                                <div id="separado">
                                    <img src={FotoAluno} alt="foto do aluno" title="foto" />
                                    <h2>{nome}</h2>
                                </div>

                                <p>{area}</p>
                            </div>

                        </div>
                        <div className="infoss espacoo">
                            <ul>
                                <div className="icon">
                                    <li><FiTrendingUp size={24} color="#4E4E4E" />Nivel de exp: {nivelExp} </li>
                                    <li><FiMap size={24} color="#4E4E4E" />{cidade}/{bairro}</li>
                                    <li><FiHome size={24} color="#4E4E4E" />Aceita Remoto: {prefRemoto }</li>
                                </div>
                            </ul>
                        </div>
                    </div>
                </div>

                <div className="centro">

                    <div className="containerr baixo">
                        <div className="listaaa espacoo">
                            <div className="aluno espacoo">
                                <div id="separado">
                                    <img src={FotoAluno} alt="foto do aluno" title="foto" />
                                    <h2>Nome Aluno</h2>
                                </div>

                                <p>Area de Foco</p>
                            </div>

                        </div>
                        <div className="infoss espacoo">
                            <ul>
                                <div className="icon">
                                    <li><FiTrendingUp size={24} color="#4E4E4E" />Nivel de exp </li>
                                    <li><FiMap size={24} color="#4E4E4E" />Estado - cidade</li>
                                    <li><FiHome size={24} color="#4E4E4E" />Aceita Remoto: Sim/Não</li>
                                </div>
                            </ul>
                        </div>
                    </div>
                </div>

            </motion.div>
            <Footer />
        </div>
    );



}

export default ListaAluno;
