import React, { useEffect, useState } from 'react';

import { FaArrowLeft } from 'react-icons/fa';

import Button from '../../components/Button';
import Input from '../../components/inputs/index';
import FotoAviso from '../../assets/images/Aviso.svg'

import '../../global.css';
import './style.css';

import { Formik, Form } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import api from '../../services/api';
import { isConstructorDeclaration } from 'typescript';




function CadastroEmpresa() {    

    

    const data = {
        nomeFantasia: '',
                    razaoSocial: '',
                    cnae: '',
                    wpp: '',
                    estado: '',
                    cidade: '',
                    cep: '',
                    bairro: '',
                    rua: '',
                    numero: ''
    }

    useEffect(() => {
        getEmpresa();
    },[])
    
    const getEmpresa = () =>{
        api.get('/empresa')
        .then(res =>{
            console.log(res)
        })
    }
    
    const cadastrar = () =>{
        api.post('/empresa',{data})
        .then(res =>{
            console.log(res)
        })
    }

    return (
        <Formik
            initialValues={
                {
                    nomeFantasia: '',
                    razaoSocial: '',
                    cnae: '',
                    wpp: '',
                    estado: '',
                    cidade: '',
                    cep: '',
                    bairro: '',
                    rua: '',
                    numero: ''
                }}
            validationSchema={Yup.object({
                nomeFantasia: Yup.string()
                    .max(20, 'Deve ter no máximo 20 caractéres')
                    .required('Required'),
                razaoSocial: Yup.string()
                    .max(20, 'Deve ter no máximo 20 caractéres')
                    .required('Required'),
                cnae: Yup.string()
                    .length(9, 'CNAE inválido')
                    .required('Required'),
                wpp: Yup.string()
                    .min(12, 'Whatsapp inválido')
                    .required('Required'),
                estado: Yup.string()
                    .min(9, 'Estado inválido')
                    .required('Required'),
                cidade: Yup.string()
                    .required('Required'),
                cep: Yup.string()
                    .notRequired(),
                bairro: Yup.string()
                    .required('Required'),
                rua: Yup.string()
                    .required('Required'),
                numero: Yup.string()
                    .required('Required'),
            })}
            onSubmit={(values, { setSubmitting }) => {
                localStorage.getItem('token-conectando');
            }}
        >
            <div className="tudo" >
                <header>
                    <div className="midCadastro">
                        <div className="arrow">
                            <FaArrowLeft color="white" size={42} />
                        </div>
                        <div className="inverter">
                            <h1>Seja bem vindo a familia SENAI!</h1><br />
                            <h3>O primeiro passo é preencher esse formulário de cadastro.</h3>
                        </div>

                        <Form>
                            <div className="tabela">
                                <div className="arrumarTabela">
                                    <h2>Seus Dados</h2>
                                    <div id="linha"></div>

                                    <Input type="text" label="Nome fantasia" name="nomeFantasia" />
                                    <Input type="text" label="Razão social" name="razaoSocial" />
                                    <Input label="CNAE" type="text" name="cnae" />
                                    <Input label="Whatsapp" name="wpp" type="text" />
                                    <h2>Seu endereço</h2>
                                    <div id="linha"></div>
                                    <div id="estadoEmpresa">
                                        <Input label="Estado" name="estado" />
                                        <Input label="Cidade" name="cidade" />
                                    </div>
                                    <Input label="CEP" name="cep" />
                                    <Input label="Bairro" name="bairro" />
                                    <div id="numeroEmpresa">
                                        <Input label="Rua" name="rua" />
                                        <Input label="Número" name="numero" />

                                    </div>
                                </div>
                            </div>
                        </Form>
                        <div className="bottom">
                            <div id="together">
                                <img src={FotoAviso} alt="aviso" />
                                <p>Preencha todos <br /> os campos</p>
                            </div>
                            <button>Cadastrar</button>
                        </div>
                    </div>
                </header>
            </div>


        </Formik>
    );
}

export default CadastroEmpresa;
