import React, { useEffect, useState } from 'react';

import { FaArrowLeft } from 'react-icons/fa';

import Button from '../../components/Button';
import Input from '../../components/inputs/index';
import FotoAviso from '../../assets/images/Aviso.svg'

import '../../global.css';
import './style.css';

import { Formik, Form } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import api from '../../services/api';
import Helmet from 'react-helmet';





function CadastroEmpresa() {

    const [idEnderecoNavigation, setIdEnderecoNavigation] = useState([]);
    const [uf, setUf] = useState("");
    const [cidade, setCidade] = useState("");
    const [cep, setCep] = useState("");
    const [rua, setRua] = useState("");
    const [bairro, setBairro] = useState("");
    const [numero, setNumero] = useState("");
    const [options, setOptions] = useState([{}, []]);

    const Schema = {
        senha: Yup.string().required("This field is required"),
        confirmarsenha: Yup.string().when("senha", {
            is: val => (val && val.length > 0 ? true : false),
            then: Yup.string().oneOf(
                [Yup.ref("senha")],
                "Both password need to be the same"
            )
        })
    };

    const GetCep = (valor: string) => {
        api.get(`https://viacep.com.br/ws/${valor}/json/`)
            .then(res => {
                var teste = res.data.cep;
                var cep = teste.replace("-", "")

                setCep(cep);
                setRua(res.data.logradouro);
                setBairro(res.data.bairro);
                setCidade(res.data.localidade)
                setEstado(res.data.uf)
            })
            .catch(erro => console.error(erro))
    }

    return (

        <Formik
            initialValues={
                {
                    nomeFantasia: '',
                    razaoSocial: '',
                    porteempresa: '',
                    email: '',
                    cnpj: '',
                    cnae: '',
                    whatsapp: '',
                    foto: '',
                    senha: '',
                    idEnderecoNavigation: {
                        uf: '',
                        cidade: '',
                        cep: '',
                        bairro: '',
                        rua: '',
                        numero: ''
                    }
                }}
            validationSchema={Yup.object({
                nomeFantasia: Yup.string()
                    .max(20, 'Deve ter no máximo 20 caracteres')
                    .required('obrigatório'),
                razaoSocial: Yup.string()
                    .max(20, 'Deve ter no máximo 20 caracteres')
                    .required('obrigatório'),
                email: Yup.string()
                    .email('Email inválido')
                    .required('obrigatório'),
                cnpj: Yup.string()
                    .max(14, 'Deve ter no máximo 14 caracteres')
                    .required('obrigatório'),
                cnae: Yup.string()
                    .max(7, 'Deve ter no máximo 9 caracteres')
                    .length(7, 'CNAE inválido')
                    .required('obrigatório'),
                whatsapp: Yup.string()
                    .min(10, 'Whatsapp inválido')
                    .required('obrigatório'),
                senha: Yup.string()
                    .required("This field is required"),
                uf: Yup.string()
                    .max(4, 'UF inválida'),
                cidade: Yup.string()
                    .max(30, 'Nome da cidade inválido'),
                cep: Yup.string()
                    .length(8, 'CEP inválido')
                    .required('Required'),
                rua: Yup.string()
                    .max(100, 'Tamanho inválido'),
                bairro: Yup.string()
                    .notRequired(),
                numero: Yup.string()
                    .max(6, 'Número maior que o devido')
                    .required('Required'),
            })}
            onSubmit={async (data, { setSubmitting }) => {
                setSubmitting(true);
                await api.post('/empresa', data)
                    .then(function (response) {
                        console.log(response);
                    })
                setSubmitting(false);
            }}
        >
            {({ values, handleBlur, handleChange, isSubmitting }) => (
                <div className="tudo" >
                    <Helmet>
                        <style>
                            {` body{
                    background-color: #F4F4F4;
                }

                    .tudo{
                        background-color: #F4F4F4;
                        
                }`}
                        </style>
                    </Helmet>

                    <header>
                        <div className="midCadastro">
                            <div className="arrow">
                                <FaArrowLeft color="white" size={42} />
                            </div>
                            <div className="inverter">
                                <h1>Seja bem vindo a familia SENAI!</h1><br />
                                <h3>O primeiro passo é preencher esse formulário de cadastro.</h3>
                            </div>

                            <Form>

                                <div className="tabelaEmpresa">
                                    <div className="arrumarTabela">
                                        <h2>Seus Dados</h2>
                                        <div id="linha"></div>

                                        <Input label="Nome fantasia" name="nomeFantasia" />
                                        <Input label="Razão social" name="razaoSocial" />
                                        <Input label="Porte da empresa" name="porteempresa" />
                                        <Input label="Email" name="email" />
                                        <Input label="CNPJ" name="cnpj" />
                                        <Input label="CNAE" name="cnae" />
                                        <Input label="Whatsapp" name="whatsapp" />
                                        <Input label="Foto" name="foto" />
                                        <Input type="password" label="Senha" name="senha" onBlur={handleBlur} onChange={handleChange} value={values.senha} />


                                        <h2>Seu endereço</h2>
                                        <div id="linha"></div>
                                        <Input label="CEP" name="cep"onBlur={(e) => GetCep(e.target.value)} />
                                        <div id="estadoEmpresa">
                                            <Input label="Cidade" name="cidade" value={cidade} onBlur={(e) => setCidade(e.target.value)}/>
                                            <Input label="UF" name="uf" value={uf} onBlur={(e) => setUf(e.target.value)} />
                                        </div>
                                        <Input label="Bairro" name="bairro" />
                                        <div id="numeroEmpresa">
                                            <Input label="Rua" name="rua" value={rua} onBlur={(e) => setRua(e.target.value)}/>
                                            <Input label="Número" name="numero"onBlur={(e) => setNumero(e.target.value)} />

                                        </div>
                                    </div>
                                </div>
                                <div className="bottomEmpresa">
                                    <div id="together">
                                        <img src={FotoAviso} alt="aviso" />
                                        <p>Preencha todos <br /> os campos</p>
                                    </div>
                                    <button type="submit" disabled={isSubmitting}>Cadastrar</button>
                                </div>

                            </Form>
                        </div>
                    </header>
                </div>
            )}
        </Formik>
    );
}

export default CadastroEmpresa;
