import { motion } from 'framer-motion';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Footer from '../../../components/footer';
import HeaderAluno from '../../../components/headerAluno';
import './style.css';
import FotoEmpresa from '../../../assets/images/micro.png';
import { FiBriefcase, FiPhone, FiGlobe, FiMapPin } from 'react-icons/fi';
import api from '../../../services/api';
import jwt_decode from "jwt-decode";

function ListaEmpresa() {

    const pageTransition = {
        duration: 1
    }

    const pageVariante = {
        in: {
            opacity: 1,
            x: 0
        },
        out: {
            opacity: 0,
            x: "-180vw"
        }
    }

    var id = "";

    function handleDecode() {
        try {
            const authDec: any = jwt_decode(auth);
            id = authDec.jti;
            return id;
        } catch (error) {
            console.log("Erro no decode")
        }
    }

    const auth = `Bearer ${localStorage.getItem('conectando-key-auth')}`;

    let decoded: any = jwt_decode(auth);

    console.log(decoded);

    useEffect(() => (load()), [])


    const [nome, setNome] = useState('');
    const [razao, setRazao] = useState('');
    const [numero, setNumero] = useState('');
    const [cnae, setCnae] = useState('');
    const [endereco, setEndereco] = useState('');

    function load() {
        api.get(`/empresa/${handleDecode()}`, {
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json',
                authorization: auth
            }
        })
            .then(function (response) {
                setNome(response.data.nomeFantasia);
                setRazao(response.data.razaoSocial);
                setCnae(response.data.cnae);
                setNumero(response.data.whatsapp);


                console.log(response.data);
            })
            .catch(function (error) {

                console.log(error);
            });
    }

    function deletar() {
        api.delete(`/empresa/${handleDecode()}`, {
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json',
                authorization: auth
            }
        }).then(function (response) {


            console.log(response.data);
        })
            .catch(function (error) {

                console.log(error);
            });
    }

    return (
        <>

            <HeaderAluno />
            <nav className="menulista">
                <ul className="menu">
                    <li ><Link className="cool-link" to="/listaAluno">Aluno</Link></li>
                    <li><Link className="cool-link" to="/listaEmpresa">Empresa</Link></li>
                    <li><Link className="cool-link" to="/listaVaga">Vagas</Link></li><br />
                </ul>
                <div className="hr">
                    <hr />
                </div>
            </nav>
            <motion.div exit="out" animate="in" initial="out" variants={pageVariante} transition={pageTransition} >

                <div className="centro">
                    <div className="containerEmpresa baixo">
                        <div className="listaa espaco">
                            <div className="empresaa espaco">
                                <h2>{nome}</h2><br />
                                <div className="ata">
                                    <img src={FotoEmpresa} alt="foto da empresa" title="foto" />
                                    <div className="partecima">
                                        <li><FiBriefcase size={24} color="#4E4E4E" />{razao} </li>
                                        <li><FiPhone size={24} color="#4E4E4E" />{numero}</li>
                                    </div>
                                    <div className="partebaixo ">
                                        <li><FiGlobe size={24} color="#4E4E4E" />{cnae}</li>
                                        <li><FiMapPin size={24} color="#4E4E4E" />{endereco}</li>
                                    </div>
                                </div>
                                <form onSubmit={event => (
                                    event.preventDefault(),
                                    deletar()
                                )}>

                                    <a>Excluir empresa</a>
                                </form>
                            </div>
                        </div>
                    </div>

                </div>





            </motion.div>
            <Footer />
        </>
    );
}

export default ListaEmpresa;
