import React, { FormEvent, useEffect, useState } from 'react'
import { FiAlertCircle, FiArrowLeft } from 'react-icons/fi';
import Footer from '../../components/footer';
import HeaderAluno from '../../components/headerAluno';
import Input from '../../components/inputs';
import perfilImg from '../../assets/images/perfil.svg';

import './styles.css';
import { Link } from 'react-router-dom';
import api from '../../services/api';
import Axios from 'axios';
import jwt_decode from 'jwt-decode';
import { Form, Formik } from 'formik';
import * as Yup from 'yup';

export interface Token {
    aud: string,
    email: string,
    exp: number,
    "http://schemas.microsoft.com/ws/2008/06/identity/claims/role": string,
    iss: string,
    jti: string,
}


function PerfilAluno(){

    const auth = `Bearer ${localStorage.getItem('conectando-key-auth')}`;
    const auth2 = localStorage.getItem('conectando-key-auth');
        var id = "";

        //#region states
        const [idAluno, setIdAluno] = useState(0);
        const [nome, setNome] = useState("");
        const [cpf, setCpf] = useState("");
        const [rg, setRg] = useState("");
        const [email, setEmail] = useState("");
        const [whatsapp, setWhatsapp] = useState("");
        const [deficiencia, setDeficiencia] = useState("");
        const [deficienciaDescricao, setDeficienciaDescricao] = useState("");
        const [senha, setSenha] = useState("");
        const [nivelExp, setNivelExp] = useState("");
        const [focoCarreira, setFocoCarreira] = useState("");
        const [idCursoNavigation, setIdCursoNavigation] = useState(0);
        const [semestre, setSemestre] = useState(0);
        const [linkPortfolio, setLinkPortfolio] = useState("");
        const [prefContrato, setPrefContrato] = useState("");
        const [prefPorte, setPrefPorte] = useState("");
        const [prefRemoto, setPrefRemoto] = useState("");
        const [idEnderecoNavigation, setIdEnderecoNavigation] = useState([]);
        const [uf,  setUf] = useState("");
        const [cidade, setCidade] = useState("");
        const [cep, setCep] = useState("");     
        const [rua, setRua] = useState("");     
        const [bairro, setBairro] = useState("");
        const [numero, setNumero] = useState("");
        //#endregion

        useEffect(() => {
            handleLoad();
        }, []);

        function handleDecode (){
            try {
                const authDec:any = jwt_decode(auth);
                console.log(authDec);
                id = authDec.jti;
                return id;
            } catch (error) {
                console.log("Erro no decode")
            }
        }

        async function handleLoad() {
            console.log(auth);
            console.log(auth2);
            
            await api.get(`/aluno/${handleDecode()}`, {
                headers: {
                    'Accept': 'application/json, text/plain, */*',
                    'Content-Type': 'application/json',
                    authorization: auth
                }
            }).then(response => {
                console.log(response.data)
                setIdAluno(response.data.idUsuario)
                setNome(response.data.nome)
                setEmail(response.data.email)
                setSenha(response.data.senha)
                setRg(response.data.rg)
                setCpf(response.data.cpf)
                setWhatsapp(response.data.whatsapp)
                setDeficiencia(response.data.deficiencia)
                setDeficienciaDescricao(response.data.deficienciaDescricao)
                setNivelExp(response.data.nivelExp)
                setFocoCarreira(response.data.focoCarreira)
                setIdCursoNavigation(response.data.idCursoNavigation)
                setSemestre(response.data.semestre)
                setLinkPortfolio(response.data.setlinkPortfolio)
                setPrefRemoto(response.data.prefRemoto)
                setPrefContrato(response.data.prefContrato)
                setPrefPorte(response.data.prefPorte)
                setIdEnderecoNavigation(response.data.idEnderecoNavigation)
                idEnderecoNavigation.forEach((endereco:any) => {
                    setUf(endereco.uf)
                    setCidade(endereco.cidade)
                    setCep(endereco.cep)
                    setRua(endereco.rua)
                    setBairro(endereco.bairro)
                    setNumero(endereco.numero)
                })
            }).catch(function (error) {
                if (error.response) {
                  // The request was made and the server responded with a status code
                  // that falls out of the range of 2xx
                    console.log(error.response.data);
                    console.log(error.response.status);
                    console.log(error.response.headers);
                } else if (error.request) {
                  // The request was made but no response was received
                  // `error.request` is an instance of XMLHttpRequest in the browser and an instance of
                  // http.ClientRequest in node.js
                    console.log(error.request);
                } else {
                  // Something happened in setting up the request that triggered an Error
                    console.log('Error', error.message);
                }
                console.log(error.config);
            });;
        }

        // async function handleUpdate(e: FormEvent) {
        //     e.preventDefault();
        //     const data = {idAluno ,nome, email, senha};
    
        //     try {
        //         await api.put(`/usuarios/${handleDecode()}`, data, {
        //             headers: {
        //                 Authorization: auth,
        //                 'Content-Type': 'application/json',
        //             }
        //         })
        //         handleLoad();
        //     } catch (err) {
        //         alert('Erro ao atualizar, tente novamente');
        //     }
        // }

        const GetCep = (valor: string) => {
            Axios.get(`https://viacep.com.br/ws/${valor}/json/`)
                .then(res => {
                    var teste = res.data.cep;
                    var cep = teste.replace("-", "")
    
                    setCep(cep);
                    setRua(res.data.logradouro);
                    setBairro(res.data.bairro);
                    setCidade(res.data.localidade)
                    setUf(res.data.uf)
                })
                .catch(erro => console.error(erro))
        }

    return(
        <div id="page-profile-form">
            <HeaderAluno/>
                <div className="topo">
                    <Link to="/">
                        <FiArrowLeft size={40} color="#4E4E4E"/>
                    </Link>
                    <div className="editarFoto">
                        <img src={perfilImg} alt=""/>
                        <span>Editar foto</span>
                    </div>
                    <div>{""}</div>
                </div>
                <main>
                <Formik
                            initialValues={
                                { 
                                    nome,
                                    cpf,
                                    rg,
                                    email,
                                    whatsapp,
                                    deficiencia,
                                    deficienciaDescricao,
                                    senha,
                                    confirmarSenha: '',
                                    uf,
                                    cidade,
                                    cep,
                                    rua,
                                    bairro,
                                    numero,
                                    nivelExp,
                                    idCursoNavigation,
                                    focoCarreira,
                                    semestre,
                                    linkPortfolio,
                                    prefContrato,
                                    prefPorte,
                                    prefRemoto,
                                }}
                            validationSchema={Yup.object({
                                nome: Yup.string()
                                .max(15, 'Deve ter no máximo 15 caractéres')
                                .required('Required'),
                                cpf: Yup.string()
                                .length(11, 'CPF inválido')
                                .required('Required'),
                                rg: Yup.string()
                                .length(12, 'Tamanho inválido')
                                .required('Required'),
                                email: Yup.string()
                                .email('email inválido')
                                .required('Required'),
                                whatsapp: Yup.string()
                                .min(9, 'Whatsapp inválidp')
                                .required('Required'),
                                deficiencia: Yup.string()
                                .notRequired(),
                                deficienciaDescricao: Yup.string()
                                .notRequired(),
                                senha: Yup.string()
                                .min(3, 'Tamanho deve ser entre 3 a 8')
                                .max(8, 'Tamanho deve ser entre 3 a 8')
                                .required('Required'),
                                confirmarSenha: Yup.string()
                                .min(3, 'Tamanho deve ser entre 3 a 8')
                                .max(8, 'Tamanho deve ser entre 3 a 8')
                                .required('Required'),
                                uf: Yup.string()
                                .max(4, 'UF inválida'),
                                cidade: Yup.string()
                                .max(30, 'Nome da cidade inválido'),
                                cep: Yup.string()
                                .length(8, 'CEP inválido')
                                .required('Required'),
                                rua: Yup.string()
                                .max(100, 'Tamanho inválido'),
                                bairro: Yup.string()
                                .notRequired(),
                                numero: Yup.string()
                                .max(6, 'Número maior que o devido')
                                .required('Required'),
                                idCurso: Yup.number()
                                .required('Required'),
                                focoCarreira: Yup.string()
                                .required('Required'),
                                semestre: Yup.number()
                                .positive('Semestre deve ser positivo')
                                .required('Required'),
                                linkPortfolio: Yup.string()
                                .notRequired(),
                                prefContrato: Yup.string()
                                .required('Required'),
                                prefPorte: Yup.string()
                                .required('Required'),
                                prefRemoto: Yup.string()
                                .required('Required'),
                            })}
                            onSubmit={async (values, { setSubmitting }) => {
                                
                            }}
                            >
                    <Form>
                        <fieldset>
                            <legend>Seus dados</legend>
                            <div>
                                <Input label="Nome" name="nome" value={nome} onChange={(e) => setNome(e.target.value)}/>
                                <Input label="CPF" name="cpf" value={cpf} onChange={(e) => setCpf(e.target.value)}/>
                                <Input label="RG" name="rg" value={rg} onChange={(e) => setRg(e.target.value)}/>
                                <Input label="Whatsapp" name="whatsapp" value={whatsapp} onChange={(e) => setWhatsapp(e.target.value)}/>
                                <div className="estadoCidade">
                                    <Input label="Estado" name="uf" value={uf} onChange={(e) => setUf(e.target.value)}/>
                                    <Input label="Cidade" name="cidade" value={cidade} onChange={(e) => setCidade(e.target.value)}/>
                                </div>
                                <Input label="Senha antiga" name="senhaAntiga" value={senha}/>
                                <div className="senha">
                                    <Input label="Nova senha" name="senha" onChange={(e) => setSenha(e.target.value)}/>
                                    <Input label="Confirmar senha" name="confirmarSenha"/>
                                </div>
                            </div>
                            <div>
                                <Input label="E-mail" name="email" value={email} onChange={(e) => setEmail(e.target.value)}/>
                                <div className="def">
                                    <Input label="Possui deficiência?" name="deficiencia" value={deficiencia} onChange={(e) => setDeficiencia(e.target.value)}/>
                                    <Input label="Se sim qual?" name="deficienciaDescricao" value={deficienciaDescricao} onChange={(e) => setDeficienciaDescricao(e.target.value)}/>
                                </div>
                                <Input label="CEP" name="cep" value={cep} onChange={(e) => GetCep(e.target.value)}/>
                                <Input label="Bairro" name="bairro" value={bairro} onChange={(e) => setBairro(e.target.value)}/>
                                <div className="ruaNumero">
                                    <Input label="Rua" name="rua" value={rua} onChange={(e) => setRua(e.target.value)}/>
                                    <Input label="Número" name="numero" value={numero} onChange={(e) => setNumero(e.target.value)}/>
                                </div>
                            </div>
                        </fieldset>
                        <fieldset>
                            <legend>Sua carreira</legend>
                            <div>
                            <Input label="Curso" name="idCurso" as="select" value={curso} onBlur={(e) => setIdCursoNavigation(parseInt(e.target.value))}>
                                    <option disabled selected value="0">Selecione uma opção</option>
                                    <option value="1">Desenvolvimento de sistemas</option>
                                    <option value="2">Redes de computadores</option>
                                    <option value="3">Multimídia</option>
                                </Input>
                                
                                <Input label="Foco na área" name="focoCarreira" value={focoCarreira} onBlur={(e) => setFocoCarreira(e.target.value)}/>
                                <Input label="Nível de experiência" name="nivelExp" value={nivelExp} onBlur={(e) => setNivelExp(e.target.value)}/>
                                <Input label="Semestre" name="semestre" as="select" value={semestre} onBlur={(e) => setSemestre(parseInt(e.target.value))}>
                                    <option disabled selected value="0">Selecione uma opção</option>
                                    <option value="1">1º semestre</option>
                                    <option value="2">2º semestre</option>
                                    <option value="3">3º semestre</option>
                                </Input>
                                <Input label="Seu portfófilo" name="linkPortfolio" value={linkPortfolio} onBlur={(e) => setLinkPortfolio(e.target.value)}/>
                                <Input label="SKILLS" name="skills"/>
                            </div>
                            <div className="buscaAlerta">
                                <div>
                                <Input label="Tipo de contrato" name="prefContrato" value={prefContrato} as="select" onBlur={(e) => setPrefContrato(e.target.value)}>
                                    <option disabled selected value="">Selecione uma opção</option>
                                    <option value="Estágio">Estágio</option>
                                    <option value="CLT">CLT</option>
                                    <option value="Freelancer">Freelancer</option>
                                </Input>
                        
                                <Input label="Tamanho da empresa" name="prefPorte" value={prefPorte} as="select" onBlur={(e) => setPrefPorte(e.target.value)}>
                                    <option disabled selected value="">Selecione uma opção</option>
                                    <option value="Pequena">Pequena</option>
                                    <option value="Média">Média</option>
                                    <option value="Grande">Grande</option>
                                </Input>

                                <Input label="Trabalho remoto?" name="prefRemoto" value={prefRemoto} as="select" onBlur={(e) => setPrefRemoto(e.target.value)}>
                                    <option disabled selected value="">Selecione uma opção</option>
                                    <option value="Não">Não</option>
                                    <option value="Sim">Sim</option>
                                </Input>
                                </div>
                                <span><FiAlertCircle size={24} color="#FF0000"/>Dados em branco ou iguais não serão alterados.</span>
                            </div>
                        </fieldset>
                        <div className="botao">
                            <button type="submit">
                                Salvar cadastro
                            </button>
                        </div>
                    </Form>
                    </Formik>
                </main>
            <Footer />
        </div>
    )
}

export default PerfilAluno;
